<div class="modal fade" id="m_scrollable_modal_1" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">New message</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <div class="m-scrollable" data-scrollbar-shown="true" data-scrollable="true" data-height="200">
            <form>
                <div class="form-group">
                    <label for="recipient-name" class="form-control-label">Recipient:</label>
                    <input type="text" class="form-control" id="recipient-name">
                </div>
                <div class="form-group">
                    <label for="recipient-name" class="form-control-label">Groups:</label>
                    <div class="m-checkbox-list">
                        <label class="m-checkbox m-checkbox--solid">
                            <input type="checkbox" checked=""> Management
                            <span></span>
                        </label>
                        <label class="m-checkbox m-checkbox--solid">
                            <input type="checkbox"> Finance
                            <span></span>
                        </label>
                        <label class="m-checkbox m-checkbox--solid">
                            <input type="checkbox"> IT Department
                            <span></span>
                        </label>
                    </div>
                </div>
                <div class="form-group">
                    <label for="message-text" class="form-control-label">Message:</label>
                    <textarea class="form-control" id="message-text" rows="6"></textarea>
                </div>
            </form>
        </div>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal"  >Close</button>
        <button type="button" class="btn btn-primary" id="m_blockui_4_1">Submit</button>
      </div>
    </div>
  </div>
</div>
